  , REG_DT
  )
  VALUES
      ( #{boardTitle}
      , #{boardContent}
      , #{boardSecretYn}
--      , #{boardType}
      , 'N'
      , #{password}
      , #{regNm}
      , #{regNm}
      , NOW()
      )

mapper.update:
UPDATE BOARD
SET INQ_CNT = COALESCE(INQ_CNT, 0) + 1
WHERE BOARD_SEQ = #{boardSeq}

mapper.select:
SELECT COUNT(*) FROM board
WHERE BOARD_DEL_YN = 'N'
AND BOARD_TYPE = #{boardType}

mapper.update:
