mapper.insert:
INSERT INTO BOARD
(BOARD_TITLE
, BOARD_CONTENT
, BOARD_SECRET_YN
, BOARD_TYPE
, BOARD_DEL_YN
, PASSWORD
, REG_NM
, REG_ID
, REG_DT
,FILE_IDX
)
VALUES
    ( #{boardTitle}
    , #{boardContent}
    , #{boardSecretYn}
    , #{boardType}
    , 'N'
    , #{password}
    , #{regNm}
    , #{regNm}
    , NOW()
    , #{fileIdx}
    )

mapper.update:
UPDATE BOARD
SET INQ_CNT = COALESCE(INQ_CNT, 0) + 1
WHERE BOARD_SEQ = #{boardSeq}

mapper.select:
SELECT COUNT(*) FROM board
WHERE BOARD_DEL_YN = 'N'
AND BOARD_TYPE = #{boardType}

mapper.update:
UPDATE BOARD
SET
    BOARD_TITLE = #{boardTitle}
  , BOARD_CONTENT = #{boardContent}
  , BOARD_SECRET_YN = #{boardSecretYn}
  , BOARD_TYPE = #{boardType}
  , BOARD_DEL_YN = 'N'
  , password = #{password}
  , UPD_NM = #{updNm}
  , UPD_ID = #{updNm}
  , UPD_DT = NOW()

      ,FILE_IDX = #{fileIdx}

WHERE BOARD_SEQ = #{boardSeq}

mapper.select:
SELECT BO.BOARD_SEQ , BO.BOARD_TYPE , BO.BOARD_TITLE , BO.BOARD_CONTENT , BO.BOARD_SECRET_YN , BO.BOARD_DEL_YN , COALESCE(BO.INQ_CNT, 0) AS INQ_CNT , BO.REG_ID , BO.REG_NM , BO.REG_DT , BO.UPD_ID , BO.UPD_NM , BO.UPD_DT , M.MENU_SEQ , M.MENU_NAME FROM BOARD BO INNER JOIN MENU M on M.MENU_SEQ = BO.BOARD_TYPE WHERE BO.BOARD_DEL_YN = 'N' AND BO.BOARD_TYPE = #{boardType}
AND BO.BOARD_TITLE LIKE CONCAT('%',#{searchBoardTitle},'%')
ORDER BY BO.BOARD_SEQ DESC LIMIT #{startIdx}, #{countPerPage}

SELECT M.MENU_NAME
 FROM MENU M
WHERE  M.MENU_BOARD_TYPE = #{boardType}

mapper.update:
UPDATE FILE
SET
    ORI_NM = #{oriNm}
  , SAVE_NM = #{saveNm}
  , FILE_PATH = #{filePath}
  , REG_DT = NOW()
WHERE FILE_IDX = #{fileIdx}

UPDATE BOARD
SET
    BANNER_NAME = #{bannerName}
  , BANNER_PARENT_SEQ = #{bannerParentSeq}
  , BANNER_LEVEL = #{bannerLevel}
  , BANNER_ORDER = #{bannerOrder}
  , BANNER_USE_YN = #{bannerUseYn}
  , BANNER_TYPE = #{bannerType}
  , BANNER_URL = #{bannerUrl}
  , BANNER_CONN_WAY = #{bannerConnWay}
  , BANNER_DESC = #{bannerDesc}
  , UPD_DT = NOW()
WHERE BANNER_SEQ = #{bannerSeq}

