mapper.insert:
INSERT INTO BOARD
(BOARD_TITLE
, BOARD_CONTENT
, BOARD_SECRET_YN
, BOARD_TYPE
, BOARD_DEL_YN
, password
, REG_NM
, REG_ID
, REG_DT
)
VALUES
    ( #{boardTitle}
    , #{boardContent}
    , #{boardSecretYn}
    , 1
    , 'N'
    , #{password}
    , #{regNm}
    , #{regNm}
    , NOW()
    )

mapper.update:
UPDATE BOARD
SET INQ_CNT = COALESCE(INQ_CNT, 0) + 1
WHERE BOARD_SEQ = #{boardSeq}

UPDATE BOARD
SET
    BOARD_TITLE = #{boardTitle}
  , BOARD_CONTENT = #{boardContent}
  , BOARD_SECRET_YN = #{boardSecretYn}
  , BOARD_TYPE = 1
  , BOARD_DEL_YN = 'N'
  , password = #{password}
  , UPD_NM = #{updNm}
  , UPD_ID = #{updNm}
  , UPD_DT = NOW()
WHERE BOARD_SEQ = #{boardSeq}
